package Before.j20220726;

public class If_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int init = 30;

		if (init == 30) {
			System.out.println("30");
		}
		if (init == 40) {
			System.out.println("40");
		}
		if (init <= 30) {
			System.out.println("30과 같거나 작다.");
		}
	}

}
