package Before.questionpool;

public class AgeIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int age = 24;
		
		if(age<=30) {
			System.out.println("20대 입니다.");
		}else if(age <=40) {
			System.out.println("30대 입니다.");
		}else if(age <=50) {
			System.out.println("40대 입니다.");
		}
		
		age = 37;
		if(age<=30) {
			System.out.println("20대 입니다.");
		}else if(age <=40) {
			System.out.println("30대 입니다.");
		}else if(age <=50) {
			System.out.println("40대 입니다.");
		}
	}
}
